import React, { useState } from 'react';

const StockInput = ({ addStockRecord }) => {
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        baseQuantity: '',
        stock: ''
    });

    const { id, name, baseQuantity, stock } = formData;

    const onChange = e => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const onSubmit = e => {
        e.preventDefault();
        addStockRecord(formData);
        setFormData({ id: '', name: '', baseQuantity: '', stock: '' });
    };

    return (
        <form onSubmit={onSubmit}>
            <div className="form-group">
                <label htmlFor="id">ID</label>
                <input type="text" name="id" value={id} onChange={onChange} required />
            </div>
            <div className="form-group">
                <label htmlFor="name">Name</label>
                <input type="text" name="name" value={name} onChange={onChange} required />
            </div>
            <div className="form-group">
                <label htmlFor="baseQuantity">Base Quantity</label>
                <input type="number" name="baseQuantity" value={baseQuantity} onChange={onChange} required />
            </div>
            <div className="form-group">
                <label htmlFor="stock">Stock</label>
                <input type="number" name="stock" value={stock} onChange={onChange} required />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
};

export default StockInput;
