import React, { useState } from 'react';

export const StockInput = ({ addStockRecord }) => {
  const [formData, setFormData] = useState({
      symbol: '',
      companyName: '',
      stockExchange: '',
  });

  const handleChange = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const resetForm = () => {
      setFormData({
          symbol: '',
          companyName: '',
          stockExchange: '',
      });
  };

  const handleSubmit = (e) => {
      e.preventDefault();
      addStockRecord(formData);
      resetForm();
  };

    const stockRecord = [
        { id: 1, name: 's', sizes: [2, 3, 4, 6, 9] },
        { id: 2, name: 'blue crown', sizes: [2, 3, 4, 6, 9] },
        { id: 3, name: 'ecofoloat', sizes: [3, 4, 6, 9] },
        { id: 4, name: 'aller-aqua', sizes: [0.2, 0.4, 0.5, 0.9, 1.3, 2, 3, 4] },
    ];

    return (
        <form onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="name">Name</label>
                <select name="name" value={formData.name} onChange={handleChange} required>
                    <option value="">Select...</option>
                    {stockRecord.map(record => (
                        <option key={record.id} value={record.name}>
                            {record.name}
                        </option>
                    ))}
                </select>
            </div>
            <div className="form-group">
                <label htmlFor="baseQuantity">Base Quantity</label>
                <input type="number" name="baseQuantity" value={formData.baseQuantity} onChange={handleInputChange} required />
            </div>
            <div className="form-group">
                <label htmlFor="stock">Stock</label>
                <input type="number" name="stock" value={formData.stock} onChange={handleInputChange} required />
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    );
};

export default StockInput;
